import React, { useEffect, useState } from 'react';
import { notificationApi } from '../services/api';

const td     = { padding: '13px 16px', fontSize: 14, color: 'var(--text-mid)', borderBottom: '1px solid var(--border)' };
const delBtn = { background: '#fff5f5', color: '#e53e3e', border: '1px solid #fed7d7', padding: '5px 13px', borderRadius: 6, fontSize: 12, cursor: 'pointer' };

export default function Notifications() {
  const [rows, setRows]         = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [form, setForm]         = useState({});
  const [error, setError]       = useState('');

  const load = () => notificationApi.getAll().then(setRows).catch(() => setRows([]));
  useEffect(() => { load(); }, []);

  const openAdd = () => { setForm({ message: '', type: '' }); setError(''); setShowModal(true); };

  const save = async () => {
    try {
      await notificationApi.create({ message: form.message, type: form.type });
      setShowModal(false); load();
    } catch { setError('Failed to save.'); }
  };

  const del = async (id) => { if (!window.confirm('Delete?')) return; await notificationApi.delete(id); load(); };

  const typeColor = (t) => {
    const m = { Alert: '#e53e3e', Info: '#4299e1', Success: '#38a169', Warning: '#ed8936' };
    return m[t] || '#718096';
  };

  const s = {
    heading: { fontFamily: "'DM Serif Display', serif", fontSize: 26, marginBottom: 6 },
    sub: { color: 'var(--text-light)', fontSize: 14, marginBottom: 24 },
    topRow: { display: 'flex', justifyContent: 'flex-end', marginBottom: 20 },
    addBtn: { background: 'var(--green-500)', color: 'white', border: 'none', padding: '9px 20px', borderRadius: 8, fontSize: 13, fontWeight: 600 },
    wrap: { background: 'var(--white)', borderRadius: 'var(--radius)', boxShadow: 'var(--shadow)', overflow: 'hidden', border: '1px solid var(--border)' },
    th: { padding: '11px 16px', textAlign: 'left', fontSize: 11, fontWeight: 700, color: 'var(--text-light)', textTransform: 'uppercase', letterSpacing: '0.06em', background: 'var(--green-50)', borderBottom: '1.5px solid var(--border)' },
    badge: (t) => ({ display: 'inline-block', padding: '3px 10px', borderRadius: 20, fontSize: 11, fontWeight: 700, background: typeColor(t) + '18', color: typeColor(t) }),
    empty: { padding: 48, textAlign: 'center', color: 'var(--text-light)' },
    modal: { position: 'fixed', inset: 0, background: 'rgba(26,46,34,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 200, backdropFilter: 'blur(2px)' },
    mbox: { background: 'var(--white)', borderRadius: 14, padding: 32, width: 460, maxWidth: '92vw', boxShadow: 'var(--shadow-md)' },
    mfooter: { display: 'flex', gap: 10, marginTop: 20, justifyContent: 'flex-end' },
    cancelBtn: { background: 'var(--bg)', color: 'var(--text-mid)', border: '1.5px solid var(--border)', padding: '9px 20px', borderRadius: 8, fontSize: 13, cursor: 'pointer' },
    saveBtn: { background: 'var(--green-500)', color: 'white', border: 'none', padding: '9px 20px', borderRadius: 8, fontSize: 13, fontWeight: 600, cursor: 'pointer' },
    flabel: { display: 'block', fontSize: 11, fontWeight: 700, color: 'var(--text-mid)', marginBottom: 6, textTransform: 'uppercase', letterSpacing: '0.05em' },
    finput: { width: '100%', padding: '10px 13px', border: '1.5px solid var(--border)', borderRadius: 8, fontSize: 14, background: 'var(--bg)', color: 'var(--text-dark)', marginBottom: 14 },
  };

  return (
    <div>
      <div style={s.heading}>Notifications</div>
      <div style={s.sub}>System alerts and budget warnings</div>
      <div style={s.topRow}><button style={s.addBtn} onClick={openAdd}>+ Add Notification</button></div>
      <div style={s.wrap}>
        {rows.length === 0
          ? <div style={s.empty}>No notifications yet.</div>
          : (
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead><tr>{['ID','Type','Message','Actions'].map(c => <th key={c} style={s.th}>{c}</th>)}</tr></thead>
              <tbody>
                {rows.map((r, i) => (
                  <tr key={r.notificationId} style={{ background: i % 2 === 0 ? 'var(--white)' : 'var(--bg)' }}>
                    <td style={td}>{r.notificationId}</td>
                    <td style={td}><span style={s.badge(r.type)}>{r.type || 'Info'}</span></td>
                    <td style={td}>{r.message}</td>
                    <td style={td}><button style={delBtn} onClick={() => del(r.notificationId)}>Delete</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
      </div>

      {showModal && (
        <div style={s.modal} onClick={e => e.target === e.currentTarget && setShowModal(false)}>
          <div style={s.mbox}>
            <div style={{ fontSize: 18, fontWeight: 700, marginBottom: 22 }}>Add Notification</div>
            {error && <div style={{ color: '#c53030', fontSize: 13, marginBottom: 14 }}>{error}</div>}
            <label style={s.flabel}>Type</label>
            <select style={s.finput} value={form.type || ''} onChange={e => setForm({...form, type: e.target.value})}>
              <option value="">Select type</option>
              {['Alert','Info','Success','Warning'].map(t => <option key={t} value={t}>{t}</option>)}
            </select>
            <label style={s.flabel}>Message</label>
            <textarea style={{ ...s.finput, height: 88, resize: 'vertical' }} placeholder="Notification message..." value={form.message || ''} onChange={e => setForm({...form, message: e.target.value})} />
            <div style={s.mfooter}>
              <button style={s.cancelBtn} onClick={() => setShowModal(false)}>Cancel</button>
              <button style={s.saveBtn} onClick={save}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
