import React, { useEffect, useState } from 'react';
import { expenseApi, userApi } from '../services/api';

const USER_ID = 1;
const td      = { padding: '13px 16px', fontSize: 14, color: 'var(--text-mid)', borderBottom: '1px solid var(--border)' };
const editBtn = { background: 'var(--green-50)', color: 'var(--green-600)', border: '1px solid var(--green-100)', padding: '5px 13px', borderRadius: 6, fontSize: 12, marginRight: 6, cursor: 'pointer' };
const delBtn  = { background: '#fff5f5', color: '#e53e3e', border: '1px solid #fed7d7', padding: '5px 13px', borderRadius: 6, fontSize: 12, cursor: 'pointer' };

export default function Expenses() {
  const [rows, setRows]         = useState([]);
  const [users, setUsers]       = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [form, setForm]         = useState({});
  const [editId, setEditId]     = useState(null);
  const [error, setError]       = useState('');

  const load = () => expenseApi.getAll().then(setRows).catch(() => setRows([]));

  useEffect(() => { load(); userApi.getAll().then(setUsers).catch(() => {}); }, []);

  const openAdd  = () => { setForm({ category: '', amount: '', date: '', description: '', userId: USER_ID }); setEditId(null); setError(''); setShowModal(true); };
  const openEdit = (r) => { setForm({ category: r.category, amount: r.amount, date: r.date, description: r.description, userId: r.user?.userId || USER_ID }); setEditId(r.expenseId); setError(''); setShowModal(true); };

  const save = async () => {
    try {
      const payload = { category: form.category, amount: parseFloat(form.amount), date: form.date, description: form.description, user: { userId: parseInt(form.userId) } };
      if (editId) await expenseApi.update(editId, payload);
      else        await expenseApi.create(payload);
      setShowModal(false); load();
    } catch { setError('Failed to save.'); }
  };

  const del = async (id) => { if (!window.confirm('Delete?')) return; await expenseApi.delete(id); load(); };

  const total = rows.reduce((s, r) => s + (r.amount || 0), 0);

  const catColor = (c) => {
    const m = { Food: '#ed8936', Travel: '#4299e1', Shopping: '#9f7aea', Other: '#718096' };
    return m[c] || '#718096';
  };

  const s = {
    heading: { fontFamily: "'DM Serif Display', serif", fontSize: 26, marginBottom: 6 },
    sub: { color: 'var(--text-light)', fontSize: 14, marginBottom: 24 },
    topRow: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20 },
    totalBadge: { background: 'var(--white)', border: '1px solid var(--border)', borderRadius: 10, padding: '10px 20px', fontSize: 14 },
    addBtn: { background: 'var(--green-500)', color: 'white', border: 'none', padding: '9px 20px', borderRadius: 8, fontSize: 13, fontWeight: 600 },
    wrap: { background: 'var(--white)', borderRadius: 'var(--radius)', boxShadow: 'var(--shadow)', overflow: 'hidden', border: '1px solid var(--border)' },
    th: { padding: '11px 16px', textAlign: 'left', fontSize: 11, fontWeight: 700, color: 'var(--text-light)', textTransform: 'uppercase', letterSpacing: '0.06em', background: 'var(--green-50)', borderBottom: '1.5px solid var(--border)' },
    badge: (c) => ({ display: 'inline-block', padding: '3px 10px', borderRadius: 20, fontSize: 11, fontWeight: 700, background: catColor(c) + '18', color: catColor(c) }),
    empty: { padding: 48, textAlign: 'center', color: 'var(--text-light)' },
    modal: { position: 'fixed', inset: 0, background: 'rgba(26,46,34,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 200, backdropFilter: 'blur(2px)' },
    mbox: { background: 'var(--white)', borderRadius: 14, padding: 32, width: 460, maxWidth: '92vw', boxShadow: 'var(--shadow-md)' },
    mfooter: { display: 'flex', gap: 10, marginTop: 20, justifyContent: 'flex-end' },
    cancelBtn: { background: 'var(--bg)', color: 'var(--text-mid)', border: '1.5px solid var(--border)', padding: '9px 20px', borderRadius: 8, fontSize: 13, cursor: 'pointer' },
    saveBtn: { background: 'var(--green-500)', color: 'white', border: 'none', padding: '9px 20px', borderRadius: 8, fontSize: 13, fontWeight: 600, cursor: 'pointer' },
    flabel: { display: 'block', fontSize: 11, fontWeight: 700, color: 'var(--text-mid)', marginBottom: 6, textTransform: 'uppercase', letterSpacing: '0.05em' },
    finput: { width: '100%', padding: '10px 13px', border: '1.5px solid var(--border)', borderRadius: 8, fontSize: 14, background: 'var(--bg)', color: 'var(--text-dark)', marginBottom: 14 },
  };

  return (
    <div>
      <div style={s.heading}>Expenses</div>
      <div style={s.sub}>Log and categorize your daily spending</div>
      <div style={s.topRow}>
        <div style={s.totalBadge}>
          <span style={{ color: 'var(--text-light)', marginRight: 8 }}>Total Expenses:</span>
          <span style={{ fontWeight: 700, color: '#e53e3e' }}>₹{total.toLocaleString()}</span>
        </div>
        <button style={s.addBtn} onClick={openAdd}>+ Log Expense</button>
      </div>
      <div style={s.wrap}>
        {rows.length === 0
          ? <div style={s.empty}>No expenses logged yet.</div>
          : (
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead><tr>{['ID','Category','Amount','Date','Description','User','Actions'].map(c => <th key={c} style={s.th}>{c}</th>)}</tr></thead>
              <tbody>
                {rows.map((r, i) => (
                  <tr key={r.expenseId} style={{ background: i % 2 === 0 ? 'var(--white)' : 'var(--bg)' }}>
                    <td style={td}>{r.expenseId}</td>
                    <td style={td}><span style={s.badge(r.category)}>{r.category}</span></td>
                    <td style={{ ...td, color: '#e53e3e', fontWeight: 600 }}>₹{r.amount?.toLocaleString()}</td>
                    <td style={td}>{r.date}</td>
                    <td style={td}>{r.description}</td>
                    <td style={td}>{r.user?.name}</td>
                    <td style={td}>
                      <button style={editBtn} onClick={() => openEdit(r)}>Edit</button>
                      <button style={delBtn}  onClick={() => del(r.expenseId)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
      </div>

      {showModal && (
        <div style={s.modal} onClick={e => e.target === e.currentTarget && setShowModal(false)}>
          <div style={s.mbox}>
            <div style={{ fontSize: 18, fontWeight: 700, marginBottom: 22 }}>{editId ? 'Edit' : 'Log'} Expense</div>
            {error && <div style={{ background: '#fff5f5', color: '#c53030', borderRadius: 8, padding: '10px 14px', fontSize: 13, marginBottom: 16 }}>{error}</div>}
            <label style={s.flabel}>Category</label>
            <select style={s.finput} value={form.category || ''} onChange={e => setForm({...form, category: e.target.value})}>
              <option value="">Select category</option>
              {['Food','Travel','Shopping','Other'].map(c => <option key={c} value={c}>{c}</option>)}
            </select>
            <label style={s.flabel}>Amount (₹)</label>
            <input style={s.finput} type="number" value={form.amount || ''} onChange={e => setForm({...form, amount: e.target.value})} />
            <label style={s.flabel}>Date</label>
            <input style={s.finput} type="date" value={form.date || ''} onChange={e => setForm({...form, date: e.target.value})} />
            <label style={s.flabel}>Description</label>
            <input style={s.finput} placeholder="What was this for?" value={form.description || ''} onChange={e => setForm({...form, description: e.target.value})} />
            <label style={s.flabel}>User</label>
            <select style={s.finput} value={form.userId || ''} onChange={e => setForm({...form, userId: e.target.value})}>
              <option value="">Select user</option>
              {users.map(u => <option key={u.userId} value={u.userId}>{u.name}</option>)}
            </select>
            <div style={s.mfooter}>
              <button style={s.cancelBtn} onClick={() => setShowModal(false)}>Cancel</button>
              <button style={s.saveBtn} onClick={save}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
