import React, { useEffect, useState } from 'react';
import { incomeApi, userApi } from '../services/api';

const USER_ID = 1;

export default function Income() {
  const [rows, setRows]       = useState([]);
  const [users, setUsers]     = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [form, setForm]       = useState({});
  const [editId, setEditId]   = useState(null);
  const [error, setError]     = useState('');

  const load = () => incomeApi.getAll().then(setRows).catch(() => setRows([]));

  useEffect(() => {
    load();
    userApi.getAll().then(setUsers).catch(() => {});
  }, []);

  const openAdd  = () => { setForm({ source: '', amount: '', date: '', userId: USER_ID }); setEditId(null); setError(''); setShowModal(true); };
  const openEdit = (r) => { setForm({ source: r.source, amount: r.amount, date: r.date, userId: r.user?.userId || USER_ID }); setEditId(r.incomeId); setError(''); setShowModal(true); };

  const save = async () => {
    try {
      const payload = { source: form.source, amount: parseFloat(form.amount), date: form.date, user: { userId: parseInt(form.userId) } };
      if (editId) await incomeApi.update(editId, payload);
      else        await incomeApi.create(payload);
      setShowModal(false); load();
    } catch { setError('Failed to save. Check all fields.'); }
  };

  const del = async (id) => { if (!window.confirm('Delete?')) return; await incomeApi.delete(id); load(); };

  const total = rows.reduce((s, r) => s + (r.amount || 0), 0);

  return <PageLayout
    title="Income" sub="Track all your income sources"
    total={total} totalLabel="Total Income" totalColor="var(--green-500)"
    onAdd={openAdd} addLabel="+ Add Income"
    columns={['ID', 'Source', 'Amount', 'Date', 'User', 'Actions']}
    rows={rows}
    renderRow={(r, i) => (
      <tr key={r.incomeId} style={{ background: i % 2 === 0 ? 'var(--white)' : 'var(--bg)' }}>
        <td style={td}>{r.incomeId}</td>
        <td style={td}>{r.source}</td>
        <td style={{ ...td, color: 'var(--green-600)', fontWeight: 600 }}>₹{r.amount?.toLocaleString()}</td>
        <td style={td}>{r.date}</td>
        <td style={td}>{r.user?.name}</td>
        <td style={td}>
          <button style={editBtn} onClick={() => openEdit(r)}>Edit</button>
          <button style={delBtn}  onClick={() => del(r.incomeId)}>Delete</button>
        </td>
      </tr>
    )}
    showModal={showModal} setShowModal={setShowModal}
    modalTitle={editId ? 'Edit Income' : 'Add Income'}
    error={error}
    form={<>
      <Field label="Source" value={form.source || ''} onChange={v => setForm({...form, source: v})} placeholder="e.g. Salary, Freelance" />
      <Field label="Amount (₹)" value={form.amount || ''} onChange={v => setForm({...form, amount: v})} type="number" />
      <Field label="Date" value={form.date || ''} onChange={v => setForm({...form, date: v})} type="date" />
      <SelectField label="User" value={form.userId || ''} onChange={v => setForm({...form, userId: v})}
        options={users.map(u => ({ value: u.userId, label: u.name }))} />
    </>}
    onSave={save}
  />;
}

// ─── Shared helpers ──────────────────────────────────────────────────────────

const td      = { padding: '13px 16px', fontSize: 14, color: 'var(--text-mid)', borderBottom: '1px solid var(--border)' };
const editBtn = { background: 'var(--green-50)', color: 'var(--green-600)', border: '1px solid var(--green-100)', padding: '5px 13px', borderRadius: 6, fontSize: 12, fontWeight: 500, marginRight: 6, cursor: 'pointer' };
const delBtn  = { background: '#fff5f5', color: '#e53e3e', border: '1px solid #fed7d7', padding: '5px 13px', borderRadius: 6, fontSize: 12, cursor: 'pointer' };

function Field({ label, value, onChange, type = 'text', placeholder = '' }) {
  return (
    <div style={{ marginBottom: 14 }}>
      <label style={{ display: 'block', fontSize: 11, fontWeight: 700, color: 'var(--text-mid)', marginBottom: 6, textTransform: 'uppercase', letterSpacing: '0.05em' }}>{label}</label>
      <input type={type} value={value} placeholder={placeholder}
        onChange={e => onChange(e.target.value)}
        style={{ width: '100%', padding: '10px 13px', border: '1.5px solid var(--border)', borderRadius: 8, fontSize: 14, background: 'var(--bg)', color: 'var(--text-dark)' }} />
    </div>
  );
}

function SelectField({ label, value, onChange, options }) {
  return (
    <div style={{ marginBottom: 14 }}>
      <label style={{ display: 'block', fontSize: 11, fontWeight: 700, color: 'var(--text-mid)', marginBottom: 6, textTransform: 'uppercase', letterSpacing: '0.05em' }}>{label}</label>
      <select value={value} onChange={e => onChange(e.target.value)}
        style={{ width: '100%', padding: '10px 13px', border: '1.5px solid var(--border)', borderRadius: 8, fontSize: 14, background: 'var(--bg)', color: 'var(--text-dark)' }}>
        <option value="">Select...</option>
        {options.map(o => <option key={o.value} value={o.value}>{o.label}</option>)}
      </select>
    </div>
  );
}

function PageLayout({ title, sub, total, totalLabel, totalColor, onAdd, addLabel, columns, rows, renderRow, showModal, setShowModal, modalTitle, error, form, onSave }) {
  const s = {
    heading: { fontFamily: "'DM Serif Display', serif", fontSize: 26, marginBottom: 6 },
    sub: { color: 'var(--text-light)', fontSize: 14, marginBottom: 24 },
    topRow: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20 },
    totalBadge: { background: 'var(--white)', border: '1px solid var(--border)', borderRadius: 10, padding: '10px 20px', fontSize: 14 },
    addBtn: { background: 'var(--green-500)', color: 'white', border: 'none', padding: '9px 20px', borderRadius: 8, fontSize: 13, fontWeight: 600, boxShadow: '0 2px 8px rgba(56,161,105,0.2)' },
    wrap: { background: 'var(--white)', borderRadius: 'var(--radius)', boxShadow: 'var(--shadow)', overflow: 'hidden', border: '1px solid var(--border)' },
    th: { padding: '11px 16px', textAlign: 'left', fontSize: 11, fontWeight: 700, color: 'var(--text-light)', textTransform: 'uppercase', letterSpacing: '0.06em', background: 'var(--green-50)', borderBottom: '1.5px solid var(--border)' },
    empty: { padding: 48, textAlign: 'center', color: 'var(--text-light)', fontSize: 14 },
    modal: { position: 'fixed', inset: 0, background: 'rgba(26,46,34,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 200, backdropFilter: 'blur(2px)' },
    mbox: { background: 'var(--white)', borderRadius: 14, padding: 32, width: 460, maxWidth: '92vw', boxShadow: 'var(--shadow-md)', maxHeight: '88vh', overflowY: 'auto' },
    mfooter: { display: 'flex', gap: 10, marginTop: 20, justifyContent: 'flex-end' },
    cancelBtn: { background: 'var(--bg)', color: 'var(--text-mid)', border: '1.5px solid var(--border)', padding: '9px 20px', borderRadius: 8, fontSize: 13, cursor: 'pointer' },
    saveBtn: { background: 'var(--green-500)', color: 'white', border: 'none', padding: '9px 20px', borderRadius: 8, fontSize: 13, fontWeight: 600, cursor: 'pointer' },
    err: { background: '#fff5f5', color: '#c53030', border: '1px solid #fed7d7', borderRadius: 8, padding: '10px 14px', fontSize: 13, marginBottom: 16 },
  };

  return (
    <div>
      <div style={s.heading}>{title}</div>
      <div style={s.sub}>{sub}</div>
      <div style={s.topRow}>
        {total !== undefined && (
          <div style={s.totalBadge}>
            <span style={{ color: 'var(--text-light)', marginRight: 8 }}>{totalLabel}:</span>
            <span style={{ fontWeight: 700, color: totalColor }}>₹{total?.toLocaleString()}</span>
          </div>
        )}
        {onAdd && <button style={s.addBtn} onClick={onAdd}>{addLabel}</button>}
      </div>
      <div style={s.wrap}>
        {rows.length === 0
          ? <div style={s.empty}>No records found. Add one to get started.</div>
          : (
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead><tr>{columns.map(c => <th key={c} style={s.th}>{c}</th>)}</tr></thead>
              <tbody>{rows.map((r, i) => renderRow(r, i))}</tbody>
            </table>
          )}
      </div>

      {showModal && (
        <div style={s.modal} onClick={e => e.target === e.currentTarget && setShowModal(false)}>
          <div style={s.mbox}>
            <div style={{ fontSize: 18, fontWeight: 700, marginBottom: 22, color: 'var(--text-dark)' }}>{modalTitle}</div>
            {error && <div style={s.err}>{error}</div>}
            {form}
            <div style={s.mfooter}>
              <button style={s.cancelBtn} onClick={() => setShowModal(false)}>Cancel</button>
              <button style={s.saveBtn} onClick={onSave}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
