import React, { useEffect, useState } from 'react';
import { goalApi } from '../services/api';
import { useAuth } from '../context/AuthContext';

const td      = { padding: '13px 16px', fontSize: 14, color: 'var(--text-mid)', borderBottom: '1px solid var(--border)' };
const editBtn = { background: 'var(--green-50)', color: 'var(--green-600)', border: '1px solid var(--green-100)', padding: '5px 13px', borderRadius: 6, fontSize: 12, marginRight: 6, cursor: 'pointer' };
const delBtn  = { background: '#fff5f5', color: '#e53e3e', border: '1px solid #fed7d7', padding: '5px 13px', borderRadius: 6, fontSize: 12, cursor: 'pointer' };

export default function Goals() {
  const { user } = useAuth();
  const USER_ID = user?.id;

  const [rows, setRows]           = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [form, setForm]           = useState({});
  const [editId, setEditId]       = useState(null);
  const [error, setError]         = useState('');

  const load = () => {
    if (!USER_ID) return;
    goalApi.getByUser(USER_ID).then(setRows).catch(() => setRows([]));
  };

  useEffect(() => { load(); }, [USER_ID]);

  const openAdd  = () => { setForm({ goalName: '', targetAmount: '', savedAmount: '', deadline: '' }); setEditId(null); setError(''); setShowModal(true); };
  const openEdit = (r) => { setForm({ goalName: r.goalName, targetAmount: r.targetAmount, savedAmount: r.savedAmount, deadline: r.deadline }); setEditId(r.goalId); setError(''); setShowModal(true); };

  const save = async () => {
    try {
      const payload = {
        goalName: form.goalName,
        targetAmount: parseFloat(form.targetAmount),
        savedAmount: parseFloat(form.savedAmount || 0),
        deadline: form.deadline,
      };
      if (editId) await goalApi.update(editId, payload);
      else        await goalApi.create(USER_ID, payload);
      setShowModal(false); load();
    } catch { setError('Failed to save.'); }
  };

  const del = async (id) => { if (!window.confirm('Delete goal?')) return; await goalApi.delete(id); load(); };

  const progress = (saved, target) => Math.min(100, Math.round(((saved || 0) / (target || 1)) * 100));

  const s = {
    heading: { fontFamily: "'DM Serif Display', serif", fontSize: 26, marginBottom: 6 },
    sub: { color: 'var(--text-light)', fontSize: 14, marginBottom: 24 },
    topRow: { display: 'flex', justifyContent: 'flex-end', marginBottom: 20 },
    addBtn: { background: 'var(--green-500)', color: 'white', border: 'none', padding: '9px 20px', borderRadius: 8, fontSize: 13, fontWeight: 600 },
    wrap: { background: 'var(--white)', borderRadius: 'var(--radius)', boxShadow: 'var(--shadow)', overflow: 'hidden', border: '1px solid var(--border)' },
    th: { padding: '11px 16px', textAlign: 'left', fontSize: 11, fontWeight: 700, color: 'var(--text-light)', textTransform: 'uppercase', letterSpacing: '0.06em', background: 'var(--green-50)', borderBottom: '1.5px solid var(--border)' },
    empty: { padding: 48, textAlign: 'center', color: 'var(--text-light)' },
    progressBar: (pct) => ({ height: 8, borderRadius: 4, background: `linear-gradient(to right, var(--green-500) ${pct}%, var(--green-100) ${pct}%)`, minWidth: 100 }),
    modal: { position: 'fixed', inset: 0, background: 'rgba(26,46,34,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 200, backdropFilter: 'blur(2px)' },
    mbox: { background: 'var(--white)', borderRadius: 14, padding: 32, width: 460, maxWidth: '92vw', boxShadow: 'var(--shadow-md)' },
    mfooter: { display: 'flex', gap: 10, marginTop: 20, justifyContent: 'flex-end' },
    cancelBtn: { background: 'var(--bg)', color: 'var(--text-mid)', border: '1.5px solid var(--border)', padding: '9px 20px', borderRadius: 8, fontSize: 13, cursor: 'pointer' },
    saveBtn: { background: 'var(--green-500)', color: 'white', border: 'none', padding: '9px 20px', borderRadius: 8, fontSize: 13, fontWeight: 600, cursor: 'pointer' },
    flabel: { display: 'block', fontSize: 11, fontWeight: 700, color: 'var(--text-mid)', marginBottom: 6, textTransform: 'uppercase', letterSpacing: '0.05em' },
    finput: { width: '100%', padding: '10px 13px', border: '1.5px solid var(--border)', borderRadius: 8, fontSize: 14, background: 'var(--bg)', color: 'var(--text-dark)', marginBottom: 14 },
  };

  return (
    <div>
      <div style={s.heading}>Savings Goals</div>
      <div style={s.sub}>Set and track your savings targets</div>
      <div style={s.topRow}>
        <button style={s.addBtn} onClick={openAdd}>+ Add Goal</button>
      </div>
      <div style={s.wrap}>
        {rows.length === 0
          ? <div style={s.empty}>No goals set yet. Start saving!</div>
          : (
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr>{['ID','Goal','Target','Saved','Deadline','Progress','Actions'].map(c => <th key={c} style={s.th}>{c}</th>)}</tr>
              </thead>
              <tbody>
                {rows.map((r, i) => {
                  const pct = progress(r.savedAmount, r.targetAmount);
                  return (
                    <tr key={r.goalId} style={{ background: i % 2 === 0 ? 'var(--white)' : 'var(--bg)' }}>
                      <td style={td}>{r.goalId}</td>
                      <td style={{ ...td, fontWeight: 600, color: 'var(--text-dark)' }}>ðŸŽ¯ {r.goalName}</td>
                      <td style={td}>â‚¹{r.targetAmount?.toLocaleString()}</td>
                      <td style={{ ...td, color: 'var(--green-600)', fontWeight: 600 }}>â‚¹{r.savedAmount?.toLocaleString()}</td>
                      <td style={td}>{r.deadline}</td>
                      <td style={td}>
                        <div style={s.progressBar(pct)} />
                        <span style={{ fontSize: 11, color: 'var(--text-light)', marginTop: 4, display: 'block' }}>{pct}%</span>
                      </td>
                      <td style={td}>
                        <button style={editBtn} onClick={() => openEdit(r)}>Edit</button>
                        <button style={delBtn}  onClick={() => del(r.goalId)}>Delete</button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
      </div>

      {showModal && (
        <div style={s.modal} onClick={e => e.target === e.currentTarget && setShowModal(false)}>
          <div style={s.mbox}>
            <div style={{ fontSize: 18, fontWeight: 700, marginBottom: 22 }}>{editId ? 'Edit' : 'Add'} Goal</div>
            {error && <div style={{ color: '#c53030', fontSize: 13, marginBottom: 14, background: '#fff5f5', padding: '10px 14px', borderRadius: 8 }}>{error}</div>}
            <label style={s.flabel}>Goal Name</label>
            <input style={s.finput} placeholder="e.g. Vacation Fund" value={form.goalName || ''} onChange={e => setForm({...form, goalName: e.target.value})} />
            <label style={s.flabel}>Target Amount (â‚¹)</label>
            <input style={s.finput} type="number" value={form.targetAmount || ''} onChange={e => setForm({...form, targetAmount: e.target.value})} />
            <label style={s.flabel}>Amount Saved So Far (â‚¹)</label>
            <input style={s.finput} type="number" value={form.savedAmount || ''} onChange={e => setForm({...form, savedAmount: e.target.value})} />
            <label style={s.flabel}>Deadline</label>
            <input style={s.finput} type="date" value={form.deadline || ''} onChange={e => setForm({...form, deadline: e.target.value})} />
            <div style={s.mfooter}>
              <button style={s.cancelBtn} onClick={() => setShowModal(false)}>Cancel</button>
              <button style={s.saveBtn} onClick={save}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
