import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

export default function Login() {
  const { login } = useAuth();
  const [form, setForm]     = useState({ email: '', password: '' });
  const [error, setError]   = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    setError(''); setLoading(true);
    try {
      const res = await axios.post('http://localhost:8080/api/auth/login', form);
      login(res.data);
    } catch {
      setError('Invalid email or password.');
    } finally {
      setLoading(false);
    }
  };

  const s = {
    page: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #edf7f0 0%, #f4f7f6 60%, #c6e8d0 100%)',
      display: 'flex', alignItems: 'center', justifyContent: 'center',
    },
    card: {
      background: '#ffffff', borderRadius: 16, padding: '44px 40px',
      width: 420, maxWidth: '92vw',
      boxShadow: '0 8px 40px rgba(56,161,105,0.14)',
      border: '1px solid var(--border)',
    },
    brand: {
      fontFamily: "'DM Serif Display', serif", fontSize: 28,
      color: 'var(--green-600)', textAlign: 'center', marginBottom: 4,
    },
    sub: { textAlign: 'center', color: 'var(--text-light)', fontSize: 13, marginBottom: 32 },
    label: {
      display: 'block', fontSize: 11, fontWeight: 700, color: 'var(--text-mid)',
      marginBottom: 6, textTransform: 'uppercase', letterSpacing: '0.05em',
    },
    input: {
      width: '100%', padding: '11px 14px',
      border: '1.5px solid var(--border)', borderRadius: 8,
      fontSize: 14, background: 'var(--bg)', color: 'var(--text-dark)',
      marginBottom: 16,
    },
    btn: {
      width: '100%', padding: '13px', background: 'var(--green-500)',
      color: 'white', border: 'none', borderRadius: 9,
      fontSize: 14, fontWeight: 700, marginTop: 4,
      boxShadow: '0 3px 12px rgba(56,161,105,0.3)',
    },
    error: {
      background: '#fff5f5', color: '#c53030',
      border: '1px solid #fed7d7', borderRadius: 8,
      padding: '10px 14px', fontSize: 13, marginBottom: 16, textAlign: 'center',
    },
    divider: {
      textAlign: 'center', color: 'var(--text-light)',
      fontSize: 12, margin: '24px 0 0', paddingTop: 20,
      borderTop: '1px solid var(--border)',
    },
  };

  return (
    <div style={s.page}>
      <div style={s.card}>
        <div style={s.brand}>ðŸ’° Budget Tracker</div>
        <div style={s.sub}>Personal Finance Manager â€” Sign In</div>

        {error && <div style={s.error}>{error}</div>}

        <label style={s.label}>Email</label>
        <input style={s.input} type="email" placeholder="Enter your email"
          value={form.email} onChange={e => setForm({ ...form, email: e.target.value })} />

        <label style={s.label}>Password</label>
        <input style={s.input} type="password" placeholder="Enter your password"
          value={form.password} onChange={e => setForm({ ...form, password: e.target.value })}
          onKeyDown={e => e.key === 'Enter' && handleLogin()} />

        <button style={s.btn} onClick={handleLogin} disabled={loading}>
          {loading ? 'Signing in...' : 'Sign In'}
        </button>

        <div style={s.divider}>
          New user? Ask your admin to register your account via the /api/users endpoint.
        </div>
      </div>
    </div>
  );
}
