import React, { useEffect, useState } from 'react';
import { balanceApi, incomeApi, expenseApi } from '../services/api';
import { useAuth } from '../context/AuthContext';

export default function Balance() {
  const { user } = useAuth();
  const USER_ID = user?.id;

  const [balance, setBalance]   = useState(null);
  const [totalInc, setTotalInc] = useState(0);
  const [totalExp, setTotalExp] = useState(0);
  const [loading, setLoading]   = useState(true);

  const load = () => {
    if (!USER_ID) return;
    setLoading(true);
    Promise.all([
      balanceApi.getByUser(USER_ID),
      incomeApi.getAll(),
      expenseApi.getAll(),
    ]).then(([bal, inc, exp]) => {
      setBalance(bal);
      setTotalInc(inc.reduce((s, i) => s + (i.amount || 0), 0));
      setTotalExp(exp.reduce((s, e) => s + (e.amount || 0), 0));
      setLoading(false);
    }).catch(() => setLoading(false));
  };

  useEffect(() => { load(); }, [USER_ID]);

  const recalc = async () => {
    await balanceApi.update(USER_ID);
    load();
  };

  const net = totalInc - totalExp;

  const s = {
    heading: { fontFamily: "'DM Serif Display', serif", fontSize: 26, marginBottom: 6 },
    sub: { color: 'var(--text-light)', fontSize: 14, marginBottom: 32 },
    grid: { display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: 20, marginBottom: 28 },
    card: (color) => ({
      background: 'var(--white)', borderRadius: 'var(--radius)', padding: '28px 24px',
      boxShadow: 'var(--shadow)', border: '1px solid var(--border)',
      borderLeft: `4px solid ${color}`,
    }),
    cardNum: (color) => ({ fontSize: 36, fontWeight: 700, color, marginBottom: 8 }),
    cardLabel: { fontSize: 13, color: 'var(--text-light)' },
    recalcBtn: { background: 'var(--green-500)', color: 'white', border: 'none', padding: '10px 24px', borderRadius: 8, fontSize: 13, fontWeight: 600, cursor: 'pointer' },
    balCard: {
      background: 'var(--white)', borderRadius: 'var(--radius)', padding: '32px',
      boxShadow: 'var(--shadow-md)', border: '1px solid var(--border)', marginBottom: 20,
      display: 'flex', alignItems: 'center', justifyContent: 'space-between',
    },
    balNum: { fontSize: 44, fontWeight: 700, color: net >= 0 ? 'var(--green-500)' : '#e53e3e' },
    balLabel: { fontSize: 14, color: 'var(--text-light)', marginTop: 6 },
  };

  return (
    <div>
      <div style={s.heading}>Balance</div>
      <div style={s.sub}>Your current financial position</div>

      {loading
        ? <div style={{ color: 'var(--text-light)' }}>Loading...</div>
        : (
          <>
            <div style={s.balCard}>
              <div>
                <div style={s.balNum}>‚Çπ{Math.abs(net).toLocaleString()}</div>
                <div style={s.balLabel}>{net >= 0 ? '‚úÖ You are in surplus' : '‚ö†Ô∏è You are in deficit'}</div>
              </div>
              <button style={s.recalcBtn} onClick={recalc}>üîÑ Recalculate</button>
            </div>

            <div style={s.grid}>
              {[
                { label: 'Total Income',   value: `‚Çπ${totalInc.toLocaleString()}`, color: 'var(--green-500)' },
                { label: 'Total Expenses', value: `‚Çπ${totalExp.toLocaleString()}`, color: '#e53e3e' },
                { label: 'Net Balance',    value: `‚Çπ${net.toLocaleString()}`,       color: net >= 0 ? 'var(--green-600)' : '#e53e3e' },
              ].map(({ label, value, color }) => (
                <div key={label} style={s.card(color)}>
                  <div style={s.cardNum(color)}>{value}</div>
                  <div style={s.cardLabel}>{label}</div>
                </div>
              ))}
            </div>

            {balance && (
              <div style={{ background: 'var(--white)', borderRadius: 'var(--radius)', padding: '20px 24px', border: '1px solid var(--border)', fontSize: 14, color: 'var(--text-mid)' }}>
                <span style={{ fontWeight: 600, color: 'var(--text-dark)' }}>Stored Balance: </span>
                ‚Çπ{balance.amount?.toLocaleString()} ‚Äî Last updated for User #{balance.user?.userId}
              </div>
            )}
          </>
        )}
    </div>
  );
}
