import React, { useEffect, useState } from 'react';
import { productApi, categoryApi, cartApi } from '../services/api';
import { useAuth } from '../context/AuthContext';

export default function Products() {
  const { user } = useAuth();
  const [products, setProducts]     = useState([]);
  const [categories, setCategories] = useState([]);
  const [filter, setFilter]         = useState('all');
  const [showModal, setShowModal]   = useState(false);
  const [form, setForm]             = useState({ price: '', quantity: '', categoryId: '' });
  const [editId, setEditId]         = useState(null);
  const [error, setError]           = useState('');
  const [addedMsg, setAddedMsg]     = useState('');

  const load = () => {
    productApi.getAll().then(setProducts).catch(() => {});
    categoryApi.getAll().then(setCategories).catch(() => {});
  };
  useEffect(() => { load(); }, []);

  const filtered = filter === 'all' ? products : products.filter(p => p.category?.categoryId === parseInt(filter));

  const openAdd  = () => { setForm({ price: '', quantity: '', categoryId: '' }); setEditId(null); setError(''); setShowModal(true); };
  const openEdit = (p) => { setForm({ price: p.price, quantity: p.quantity, categoryId: p.category?.categoryId || '' }); setEditId(p.productId); setError(''); setShowModal(true); };

  const save = async () => {
    try {
      const payload = { price: parseFloat(form.price), quantity: parseInt(form.quantity), category: { categoryId: parseInt(form.categoryId) } };
      if (editId) await productApi.update(editId, payload);
      else        await productApi.create(payload);
      setShowModal(false); load();
    } catch { setError('Failed to save.'); }
  };

  const del = async (id) => { if (!window.confirm('Delete product?')) return; await productApi.delete(id); load(); };

  const addToCart = async (product) => {
    try {
      const carts = await cartApi.getAll();
      let myCart = carts.find(c => c.customer?.customerId === user?.id);
      const currentTotal = myCart ? myCart.totalPrice + product.price : product.price;
      const discount = currentTotal > 200 ? 25 : 0;
      if (myCart) {
        await cartApi.update(myCart.cartId, { ...myCart, totalPrice: currentTotal, discount, customer: { customerId: user.id } });
      } else {
        await cartApi.create({ totalPrice: product.price, discount: 0, customer: { customerId: user.id } });
      }
      setAddedMsg(`${product.category?.categoryType || 'Product'} added to cart!`);
      setTimeout(() => setAddedMsg(''), 2500);
    } catch { alert('Failed to add to cart.'); }
  };

  const catEmoji = (type) => ({ Fruits:'üçé', Vegetables:'ü•¶', Dairy:'ü•õ', Bakery:'üçû', Beverages:'ü•§', Snacks:'üçø', Meat:'ü•©' }[type] || 'üõí');

  const s = {
    heading: { fontFamily: "'Playfair Display', serif", fontSize: 26, marginBottom: 4 },
    sub: { color: '#a8a29e', fontSize: 14, marginBottom: 24 },
    topRow: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20, flexWrap: 'wrap', gap: 10 },
    filterRow: { display: 'flex', gap: 8, flexWrap: 'wrap' },
    filterBtn: (active) => ({
      padding: '6px 14px', borderRadius: 20, fontSize: 12, fontWeight: 600,
      border: active ? '2px solid #f97316' : '2px solid #e7e5e4',
      background: active ? '#fff7ed' : '#fff',
      color: active ? '#f97316' : '#78716c', cursor: 'pointer',
    }),
    addBtn: { background: '#f97316', color: 'white', border: 'none', padding: '9px 18px', borderRadius: 8, fontSize: 13, fontWeight: 700, cursor: 'pointer' },
    grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: 16 },
    card: { background: '#fff', borderRadius: 14, overflow: 'hidden', boxShadow: '0 2px 12px rgba(0,0,0,0.06)', border: '1px solid #e7e5e4', transition: 'transform 0.15s' },
    cardTop: { height: 110, display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 50, background: '#fff7ed' },
    cardBody: { padding: '14px' },
    catLabel: { fontSize: 11, color: '#f97316', fontWeight: 700, textTransform: 'uppercase', letterSpacing: '0.05em', marginBottom: 4 },
    price: { fontSize: 22, fontWeight: 700, color: '#1c1917', marginBottom: 4 },
    qty: { fontSize: 12, color: '#a8a29e', marginBottom: 12 },
    cardBtns: { display: 'flex', gap: 6 },
    cartBtn: { flex: 1, background: '#f97316', color: 'white', border: 'none', padding: '8px', borderRadius: 7, fontSize: 12, fontWeight: 700, cursor: 'pointer' },
    editBtn: { background: '#fff7ed', color: '#f97316', border: '1px solid #fed7aa', padding: '8px 10px', borderRadius: 7, fontSize: 12, cursor: 'pointer' },
    delBtn:  { background: '#fff5f5', color: '#dc2626', border: '1px solid #fecaca', padding: '8px 10px', borderRadius: 7, fontSize: 12, cursor: 'pointer' },
    toast: { position: 'fixed', bottom: 28, left: '50%', transform: 'translateX(-50%)', background: '#16a34a', color: 'white', padding: '12px 24px', borderRadius: 10, fontSize: 14, fontWeight: 600, zIndex: 999, boxShadow: '0 4px 16px rgba(0,0,0,0.15)' },
    modal: { position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.4)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 200 },
    mbox: { background: '#fff', borderRadius: 16, padding: 32, width: 400, maxWidth: '92vw' },
    flabel: { display: 'block', fontSize: 11, fontWeight: 700, color: '#78716c', marginBottom: 5, textTransform: 'uppercase' },
    finput: { width: '100%', padding: '10px 12px', border: '1.5px solid #e7e5e4', borderRadius: 8, fontSize: 14, marginBottom: 13, background: '#fafaf9', boxSizing: 'border-box' },
    saveBtn: { background: '#f97316', color: 'white', border: 'none', padding: '10px 20px', borderRadius: 8, fontSize: 13, fontWeight: 700, cursor: 'pointer' },
    cancelBtn: { background: '#fafaf9', color: '#78716c', border: '1.5px solid #e7e5e4', padding: '10px 20px', borderRadius: 8, fontSize: 13, cursor: 'pointer', marginRight: 8 },
  };

  return (
    <div>
      <div style={s.heading}>Browse Products üõçÔ∏è</div>
      <div style={s.sub}>Fresh groceries available for delivery</div>

      <div style={s.topRow}>
        <div style={s.filterRow}>
          <button style={s.filterBtn(filter === 'all')} onClick={() => setFilter('all')}>All</button>
          {categories.map(c => (
            <button key={c.categoryId} style={s.filterBtn(filter === c.categoryId)} onClick={() => setFilter(c.categoryId)}>
              {catEmoji(c.categoryType)} {c.categoryType}
            </button>
          ))}
        </div>
        <button style={s.addBtn} onClick={openAdd}>+ Add Product</button>
      </div>

      {filtered.length === 0
        ? <div style={{ textAlign: 'center', color: '#a8a29e', padding: 48 }}>No products found.</div>
        : <div style={s.grid}>
            {filtered.map(p => (
              <div key={p.productId} style={s.card}>
                <div style={s.cardTop}>{catEmoji(p.category?.categoryType)}</div>
                <div style={s.cardBody}>
                  <div style={s.catLabel}>{p.category?.categoryType || 'Uncategorized'}</div>
                  <div style={s.price}>‚Çπ{p.price?.toLocaleString()}</div>
                  <div style={s.qty}>Qty: {p.quantity} available</div>
                  <div style={s.cardBtns}>
                    <button style={s.cartBtn} onClick={() => addToCart(p)}>üõí Add</button>
                    <button style={s.editBtn} onClick={() => openEdit(p)}>‚úèÔ∏è</button>
                    <button style={s.delBtn}  onClick={() => del(p.productId)}>üóë</button>
                  </div>
                </div>
              </div>
            ))}
          </div>}

      {addedMsg && <div style={s.toast}>‚úÖ {addedMsg}</div>}

      {showModal && (
        <div style={s.modal} onClick={e => e.target === e.currentTarget && setShowModal(false)}>
          <div style={s.mbox}>
            <div style={{ fontSize: 17, fontWeight: 700, marginBottom: 20 }}>{editId ? 'Edit' : 'Add'} Product</div>
            {error && <div style={{ color: '#dc2626', fontSize: 13, marginBottom: 12 }}>{error}</div>}
            <label style={s.flabel}>Category</label>
            <select style={s.finput} value={form.categoryId} onChange={e => setForm({...form, categoryId: e.target.value})}>
              <option value="">Select category</option>
              {categories.map(c => <option key={c.categoryId} value={c.categoryId}>{c.categoryType}</option>)}
            </select>
            <label style={s.flabel}>Price (‚Çπ)</label>
            <input style={s.finput} type="number" placeholder="e.g. 49" value={form.price} onChange={e => setForm({...form, price: e.target.value})} />
            <label style={s.flabel}>Quantity Available</label>
            <input style={s.finput} type="number" placeholder="e.g. 100" value={form.quantity} onChange={e => setForm({...form, quantity: e.target.value})} />
            <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: 8 }}>
              <button style={s.cancelBtn} onClick={() => setShowModal(false)}>Cancel</button>
              <button style={s.saveBtn} onClick={save}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
