import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { patientApi, doctorApi, appointmentApi, reportApi } from '../services/api';

const s = {
  heading: { fontFamily: "'DM Serif Display', serif", fontSize: 28, color: 'var(--text-dark)', marginBottom: 6 },
  sub: { color: 'var(--text-light)', fontSize: 14, marginBottom: 32 },
  grid: { display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: 20, marginBottom: 32 },
  card: { background: 'var(--white)', borderRadius: 'var(--radius)', padding: '22px 24px', boxShadow: 'var(--shadow)', borderTop: '3px solid var(--blue-400)' },
  cardNum: { fontSize: 32, fontWeight: 600, color: 'var(--blue-600)' },
  cardLabel: { fontSize: 13, color: 'var(--text-light)', marginTop: 4 },
  section: { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 24 },
  panel: { background: 'var(--white)', borderRadius: 'var(--radius)', padding: 24, boxShadow: 'var(--shadow)' },
  panelTitle: { fontSize: 16, fontWeight: 600, marginBottom: 16, color: 'var(--text-dark)' },
  flowItem: { display: 'flex', alignItems: 'flex-start', gap: 14, marginBottom: 16 },
  flowNum: { width: 28, height: 28, borderRadius: '50%', background: 'var(--blue-500)', color: 'white', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 12, fontWeight: 700, flexShrink: 0 },
  flowText: { fontSize: 13, color: 'var(--text-mid)', lineHeight: 1.5 },
  flowBold: { fontWeight: 600, color: 'var(--text-dark)', display: 'block', marginBottom: 2 },
  quickLink: { display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '12px 14px', borderRadius: 8, background: 'var(--blue-50)', marginBottom: 8, fontSize: 13, color: 'var(--blue-700)', fontWeight: 500 },
};

const flows = [
  { title: 'Patient Registration', desc: 'Patient creates account with name, age, and contact details' },
  { title: 'Health Data Entry', desc: 'Patient enters past records & daily readings (heart rate, BP, oxygen, temperature)' },
  { title: 'Data Review by Doctor', desc: 'Doctor logs in, views health records, and checks trends' },
  { title: 'Doctor Consultation', desc: 'Doctor reviews data remotely and sends advice or schedules appointments' },
  { title: 'Medical History Access', desc: 'Both patient and doctor can view past records and reports' },
  { title: 'Feedback & Reports', desc: 'Patient receives feedback messages and stored reports for reference' },
];

export default function Dashboard() {
  const [counts, setCounts] = useState({ patients: '-', doctors: '-', appointments: '-', reports: '-' });

  useEffect(() => {
    Promise.all([patientApi.getAll(), doctorApi.getAll(), appointmentApi.getAll(), reportApi.getAll()])
      .then(([p, d, a, r]) => setCounts({ patients: p.length, doctors: d.length, appointments: a.length, reports: r.length }))
      .catch(() => {});
  }, []);

  return (
    <div>
      <div style={s.heading}>Patient Monitoring System</div>
      <div style={s.sub}>Overview of all health management activities</div>

      <div style={s.grid}>
        {[
          { label: 'Patients', count: counts.patients, to: '/patients' },
          { label: 'Doctors', count: counts.doctors, to: '/doctors' },
          { label: 'Appointments', count: counts.appointments, to: '/appointments' },
          { label: 'Reports', count: counts.reports, to: '/reports' },
        ].map(({ label, count, to }) => (
          <Link to={to} key={label} style={{ textDecoration: 'none' }}>
            <div style={s.card}>
              <div style={s.cardNum}>{count}</div>
              <div style={s.cardLabel}>{label}</div>
            </div>
          </Link>
        ))}
      </div>

      <div style={s.section}>
        <div style={s.panel}>
          <div style={s.panelTitle}>System Flow</div>
          {flows.map((f, i) => (
            <div key={i} style={s.flowItem}>
              <div style={s.flowNum}>{i + 1}</div>
              <div style={s.flowText}>
                <span style={s.flowBold}>{f.title}</span>
                {f.desc}
              </div>
            </div>
          ))}
        </div>

        <div style={s.panel}>
          <div style={s.panelTitle}>Quick Access</div>
          {[
            { to: '/patients', label: 'Manage Patients', icon: 'ðŸ‘¤' },
            { to: '/doctors', label: 'Manage Doctors', icon: 'ðŸ©º' },
            { to: '/appointments', label: 'View Appointments', icon: 'ðŸ“…' },
            { to: '/health-data', label: 'Health Records', icon: 'â¤ï¸' },
            { to: '/readings', label: 'Daily Readings', icon: 'ðŸ“Š' },
            { to: '/consultations', label: 'Consultations', icon: 'ðŸ’¬' },
            { to: '/messages', label: 'Messages', icon: 'âœ‰ï¸' },
            { to: '/reports', label: 'Reports', icon: 'ðŸ“‹' },
          ].map(({ to, label, icon }) => (
            <Link to={to} key={to} style={{ textDecoration: 'none' }}>
              <div style={s.quickLink}>
                <span>{icon} {label}</span>
                <span style={{ fontSize: 16, color: 'var(--blue-400)' }}>â†’</span>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}
