import React from 'react';
import CrudTable from '../components/CrudTable';
import { dailyReadingApi } from '../services/api';

const fields = [
  { key: 'readingId', label: 'ID', noForm: true },
  { key: 'patientId', label: 'Patient ID', type: 'number' },
  { key: 'readingDate', label: 'Date', type: 'date' },
  { key: 'heartRate', label: 'Heart Rate (bpm)', type: 'number' },
  { key: 'bloodPressure', label: 'Blood Pressure', placeholder: 'e.g. 120/80' },
  { key: 'oxygenLevel', label: 'Oxygen Level (%)', type: 'number' },
  { key: 'temperature', label: 'Temperature (°F)', type: 'number' },
];

export default function DailyReadings() {
  return (
    <div>
      <h1 style={{ fontFamily: "'DM Serif Display', serif", fontSize: 26, marginBottom: 6 }}>Daily Readings</h1>
      <p style={{ color: 'var(--text-light)', marginBottom: 24, fontSize: 14 }}>Track daily vitals — heart rate, blood pressure, oxygen, temperature</p>
      <CrudTable title="Daily Readings" fields={fields} api={dailyReadingApi} idField="readingId" />
    </div>
  );
}
