import React from 'react';
import CrudTable from '../components/CrudTable';
import { appointmentApi } from '../services/api';

const fields = [
  { key: 'appointId', label: 'ID', noForm: true },
  { key: 'patientId', label: 'Patient ID', type: 'number' },
  { key: 'doctorId', label: 'Doctor ID', type: 'number' },
  { key: 'appointmentDate', label: 'Date', type: 'date' },
  { key: 'appointmentTime', label: 'Time', type: 'time' },
  { key: 'status', label: 'Status', placeholder: 'Scheduled / Completed / Cancelled' },
  { key: 'notes', label: 'Notes' },
];

export default function Appointments() {
  return (
    <div>
      <h1 style={{ fontFamily: "'DM Serif Display', serif", fontSize: 26, marginBottom: 6 }}>Appointments</h1>
      <p style={{ color: 'var(--text-light)', marginBottom: 24, fontSize: 14 }}>Schedule and manage doctor-patient appointments</p>
      <CrudTable title="Appointments" fields={fields} api={appointmentApi} idField="appointId" />
    </div>
  );
}
