import React from 'react';
import CrudTable from '../components/CrudTable';
import { patientApi } from '../services/api';

const fields = [
  { key: 'patientId', label: 'ID', noForm: true },
  { key: 'name', label: 'Name', placeholder: 'Full Name' },
  { key: 'age', label: 'Age', type: 'number' },
  { key: 'gender', label: 'Gender', placeholder: 'Male / Female' },
  { key: 'contact', label: 'Contact', placeholder: 'Phone number' },
  { key: 'email', label: 'Email', type: 'email' },
  { key: 'address', label: 'Address' },
];

export default function Patients() {
  return (
    <div>
      <h1 style={{ fontFamily: "'DM Serif Display', serif", fontSize: 26, marginBottom: 6 }}>Patients</h1>
      <p style={{ color: 'var(--text-light)', marginBottom: 24, fontSize: 14 }}>Manage patient registrations and personal details</p>
      <CrudTable title="Patients" fields={fields} api={patientApi} idField="patientId" />
    </div>
  );
}
