import axios from 'axios';

const BASE = 'http://localhost:8080/api';
const api = axios.create({ baseURL: BASE });

// Generic CRUD factory
const crud = (path) => ({
  getAll: () => api.get(`/${path}`).then(r => r.data),
  getById: (id) => api.get(`/${path}/${id}`).then(r => r.data),
  create: (data) => api.post(`/${path}`, data).then(r => r.data),
  update: (id, data) => api.put(`/${path}/${id}`, data).then(r => r.data),
  delete: (id) => api.delete(`/${path}/${id}`),
});

export const patientApi = crud('patients');
export const doctorApi = crud('doctors');
export const appointmentApi = crud('appointments');
export const healthDataApi = crud('health-data');
export const dailyReadingApi = crud('readings');
export const consultationApi = crud('consultations');
export const messageApi = crud('messages');
export const reportApi = crud('reports');
