import React from 'react';
import CrudTable from '../components/CrudTable';
import { messageApi } from '../services/api';

const fields = [
  { key: 'messageId', label: 'ID', noForm: true },
  { key: 'senderId', label: 'Sender ID', type: 'number' },
  { key: 'receiverId', label: 'Receiver ID', type: 'number' },
  { key: 'senderType', label: 'Sender Type', placeholder: 'Patient / Doctor' },
  { key: 'messageText', label: 'Message', placeholder: 'Write your message...' },
  { key: 'sentAt', label: 'Sent At', type: 'datetime-local' },
];

export default function Messages() {
  return (
    <div>
      <h1 style={{ fontFamily: "'DM Serif Display', serif", fontSize: 26, marginBottom: 6 }}>Messages</h1>
      <p style={{ color: 'var(--text-light)', marginBottom: 24, fontSize: 14 }}>Communication between doctors and patients</p>
      <CrudTable title="Messages" fields={fields} api={messageApi} idField="messageId" />
    </div>
  );
}
